import React, { useState } from 'react';
import { 
  Container, 
  Box, 
  TextField, 
  Button, 
  Typography, 
  Card, 
  CardContent,
  Grid,
  CircularProgress,
  Divider
} from '@mui/material';
import { styled } from '@mui/material/styles';
import SearchIcon from '@mui/icons-material/Search';
import WbSunnyIcon from '@mui/icons-material/WbSunny';
import CloudIcon from '@mui/icons-material/Cloud';
import WaterDropIcon from '@mui/icons-material/WaterDrop';
import AirIcon from '@mui/icons-material/Air';
import ThermostatIcon from '@mui/icons-material/Thermostat';
import CompressIcon from '@mui/icons-material/Compress';
import VisibilityIcon from '@mui/icons-material/Visibility';
import { WEATHER_API_KEY, WEATHER_API_BASE_URL } from './config';

const StyledContainer = styled(Container)(({ theme }) => ({
  minHeight: '100vh',
  paddingTop: theme.spacing(4),
  background: 'linear-gradient(135deg, #00b4db 0%, #0083b0 100%)',
}));

const WeatherCard = styled(Card)(({ theme }) => ({
  background: 'rgba(255, 255, 255, 0.9)',
  backdropFilter: 'blur(10px)',
  borderRadius: '20px',
  boxShadow: '0 8px 32px 0 rgba(31, 38, 135, 0.37)',
  marginTop: theme.spacing(4),
}));

const SearchBox = styled(Box)(({ theme }) => ({
  display: 'flex',
  gap: theme.spacing(2),
  marginBottom: theme.spacing(4),
}));

const WeatherInfo = styled(Box)(({ theme }) => ({
  display: 'flex',
  alignItems: 'center',
  gap: theme.spacing(1),
  marginTop: theme.spacing(2),
}));

function App() {
  const [city, setCity] = useState('');
  const [weather, setWeather] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSearch = async () => {
    if (!city) return;

    setLoading(true);
    setError('');

    try {
      const response = await fetch(
        `${WEATHER_API_BASE_URL}/weather?q=${city}&appid=${WEATHER_API_KEY}&units=metric`
      );
      const data = await response.json();

      if (response.ok) {
        setWeather(data);
      } else {
        setError(data.message);
      }
    } catch (err) {
      setError('Failed to fetch weather data');
    } finally {
      setLoading(false);
    }
  };

  const getWeatherIcon = (weatherCode) => {
    // Weather condition codes from OpenWeatherMap
    if (weatherCode >= 200 && weatherCode < 300) return '⛈️'; // Thunderstorm
    if (weatherCode >= 300 && weatherCode < 400) return '🌧️'; // Drizzle
    if (weatherCode >= 500 && weatherCode < 600) return '🌦️'; // Rain
    if (weatherCode >= 600 && weatherCode < 700) return '🌨️'; // Snow
    if (weatherCode >= 700 && weatherCode < 800) return '🌫️'; // Atmosphere
    if (weatherCode === 800) return '☀️'; // Clear
    if (weatherCode > 800) return '☁️'; // Clouds
    return '❓';
  };

  return (
    <StyledContainer>
      <Typography variant="h3" component="h1" align="center" gutterBottom sx={{ color: 'white' }}>
        Weather App
      </Typography>

      <SearchBox>
        <TextField
          fullWidth
          variant="outlined"
          placeholder="Enter city name"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
          sx={{ 
            backgroundColor: 'rgba(255, 255, 255, 0.9)',
            borderRadius: '10px',
            '& .MuiOutlinedInput-root': {
              borderRadius: '10px',
            }
          }}
        />
        <Button
          variant="contained"
          onClick={handleSearch}
          disabled={loading}
          sx={{
            borderRadius: '10px',
            backgroundColor: '#0083b0',
            '&:hover': {
              backgroundColor: '#006d94',
            }
          }}
        >
          <SearchIcon />
        </Button>
      </SearchBox>

      {error && (
        <Typography color="error" align="center" gutterBottom>
          {error}
        </Typography>
      )}

      {loading ? (
        <Box display="flex" justifyContent="center" mt={4}>
          <CircularProgress />
        </Box>
      ) : weather && (
        <WeatherCard>
          <CardContent>
            <Typography variant="h4" component="h2" gutterBottom align="center">
              {weather.name}, {weather.sys.country}
            </Typography>

            <Box display="flex" justifyContent="center" mb={2}>
              <Typography variant="h2" component="div">
                {getWeatherIcon(weather.weather[0].id)}
              </Typography>
            </Box>

            <Grid container spacing={3}>
              <Grid item xs={12} md={6}>
                <Box display="flex" flexDirection="column" alignItems="center">
                  <Typography variant="h1" component="div">
                    {Math.round(weather.main.temp)}°C
                  </Typography>
                  <Typography variant="h5" color="textSecondary" sx={{ textTransform: 'capitalize' }}>
                    {weather.weather[0].description}
                  </Typography>
                </Box>
              </Grid>

              <Grid item xs={12} md={6}>
                <Box>
                  <WeatherInfo>
                    <ThermostatIcon />
                    <Typography>Feels like: {Math.round(weather.main.feels_like)}°C</Typography>
                  </WeatherInfo>
                  <WeatherInfo>
                    <WaterDropIcon />
                    <Typography>Humidity: {weather.main.humidity}%</Typography>
                  </WeatherInfo>
                  <WeatherInfo>
                    <AirIcon />
                    <Typography>Wind: {weather.wind.speed} m/s</Typography>
                  </WeatherInfo>
                  <WeatherInfo>
                    <CompressIcon />
                    <Typography>Pressure: {weather.main.pressure} hPa</Typography>
                  </WeatherInfo>
                  <WeatherInfo>
                    <VisibilityIcon />
                    <Typography>Visibility: {(weather.visibility / 1000).toFixed(1)} km</Typography>
                  </WeatherInfo>
                </Box>
              </Grid>
            </Grid>

            <Divider sx={{ my: 3 }} />

            <Grid container spacing={2} justifyContent="center">
              <Grid item xs={6} sm={3}>
                <Box textAlign="center">
                  <Typography variant="h6" color="textSecondary">
                    Sunrise
                  </Typography>
                  <Typography>
                    {new Date(weather.sys.sunrise * 1000).toLocaleTimeString()}
                  </Typography>
                </Box>
              </Grid>
              <Grid item xs={6} sm={3}>
                <Box textAlign="center">
                  <Typography variant="h6" color="textSecondary">
                    Sunset
                  </Typography>
                  <Typography>
                    {new Date(weather.sys.sunset * 1000).toLocaleTimeString()}
                  </Typography>
                </Box>
              </Grid>
            </Grid>
          </CardContent>
        </WeatherCard>
      )}
    </StyledContainer>
  );
}

export default App;
