import React, { useState } from 'react';
import { 
  Container, 
  Box, 
  TextField, 
  Button, 
  Typography, 
  Card, 
  CardContent,
  Grid,
  CircularProgress,
  Divider,
  Alert,
  IconButton,
  ThemeProvider,
  createTheme,
  CssBaseline
} from '@mui/material';
import { styled } from '@mui/material/styles';
import SearchIcon from '@mui/icons-material/Search';
import WbSunnyIcon from '@mui/icons-material/WbSunny';
import CloudIcon from '@mui/icons-material/Cloud';
import WaterDropIcon from '@mui/icons-material/WaterDrop';
import AirIcon from '@mui/icons-material/Air';
import ThermostatIcon from '@mui/icons-material/Thermostat';
import CompressIcon from '@mui/icons-material/Compress';
import VisibilityIcon from '@mui/icons-material/Visibility';
import Brightness4Icon from '@mui/icons-material/Brightness4';
import Brightness7Icon from '@mui/icons-material/Brightness7';
import { OPEN_METEO_BASE_URL } from './config';

const StyledContainer = styled(Container)(({ theme }) => ({
  minHeight: '100vh',
  paddingTop: theme.spacing(4),
  position: 'relative',
  zIndex: 1,
  background: theme.palette.mode === 'dark'
    ? 'linear-gradient(135deg, #1a237e 0%, #0d47a1 100%)'
    : 'linear-gradient(135deg, #00b4db 0%, #0083b0 100%)',
  transition: 'all 0.3s ease-in-out',
  overflow: 'hidden',
  '&::before': {
    content: '""',
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    zIndex: -1,
    background: theme.palette.mode === 'dark'
      ? 'radial-gradient(circle at 100% 50%, transparent 20%, rgba(255,255,255,.025) 21%, rgba(255,255,255,.025) 34%, transparent 35%, transparent), radial-gradient(circle at 0% 50%, transparent 20%, rgba(255,255,255,.025) 21%, rgba(255,255,255,.025) 34%, transparent 35%, transparent) 0 -50px'
      : 'radial-gradient(circle at 100% 50%, transparent 20%, rgba(255,255,255,.3) 21%, rgba(255,255,255,.3) 34%, transparent 35%, transparent), radial-gradient(circle at 0% 50%, transparent 20%, rgba(255,255,255,.3) 21%, rgba(255,255,255,.3) 34%, transparent 35%, transparent) 0 -50px',
    backgroundSize: '75px 100px',
    opacity: 0.6,
    animation: 'wave 8s linear infinite',
  },
  '&::after': {
    content: '""',
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    zIndex: -2,
    background: theme.palette.mode === 'dark'
      ? 'radial-gradient(circle at 100% 50%, transparent 20%, rgba(255,255,255,.025) 21%, rgba(255,255,255,.025) 34%, transparent 35%, transparent), radial-gradient(circle at 0% 50%, transparent 20%, rgba(255,255,255,.025) 21%, rgba(255,255,255,.025) 34%, transparent 35%, transparent) 0 -50px'
      : 'radial-gradient(circle at 100% 50%, transparent 20%, rgba(255,255,255,.3) 21%, rgba(255,255,255,.3) 34%, transparent 35%, transparent), radial-gradient(circle at 0% 50%, transparent 20%, rgba(255,255,255,.3) 21%, rgba(255,255,255,.3) 34%, transparent 35%, transparent) 0 -50px',
    backgroundSize: '75px 100px',
    opacity: 0.4,
    transform: 'scaleX(-1)',
    animation: 'wave 12s linear infinite',
    filter: 'blur(2px)',
  },
  '& .background-particles': {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    zIndex: -1,
    '&::before, &::after': {
      content: '""',
      position: 'absolute',
      width: '100%',
      height: '100%',
      backgroundImage: theme.palette.mode === 'dark'
        ? 'radial-gradient(circle at center, rgba(255,255,255,0.1) 0%, transparent 50%)'
        : 'radial-gradient(circle at center, rgba(255,255,255,0.2) 0%, transparent 50%)',
      animation: 'pulse 4s ease-in-out infinite',
    },
    '&::after': {
      animationDelay: '2s',
    }
  },
  '& .background-overlay': {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    zIndex: -1,
    background: theme.palette.mode === 'dark'
      ? 'linear-gradient(45deg, rgba(0,0,0,0.2) 0%, rgba(0,0,0,0) 100%)'
      : 'linear-gradient(45deg, rgba(255,255,255,0.2) 0%, rgba(255,255,255,0) 100%)',
  },
  '@keyframes wave': {
    '0%': {
      backgroundPosition: '0 0',
    },
    '100%': {
      backgroundPosition: '0 -100px',
    },
  },
  '@keyframes pulse': {
    '0%, 100%': {
      transform: 'scale(1)',
      opacity: 0.5,
    },
    '50%': {
      transform: 'scale(1.1)',
      opacity: 0.8,
    },
  }
}));

const WeatherCard = styled(Card)(({ theme }) => ({
  background: theme.palette.mode === 'dark'
    ? 'rgba(30, 30, 30, 0.9)'
    : 'rgba(255, 255, 255, 0.9)',
  backdropFilter: 'blur(10px)',
  borderRadius: '20px',
  boxShadow: theme.palette.mode === 'dark'
    ? '0 8px 32px 0 rgba(0, 0, 0, 0.37)'
    : '0 8px 32px 0 rgba(31, 38, 135, 0.37)',
  marginTop: theme.spacing(4),
}));

const SearchBox = styled(Box)(({ theme }) => ({
  display: 'flex',
  gap: theme.spacing(2),
  marginBottom: theme.spacing(4),
}));

const WeatherInfo = styled(Box)(({ theme }) => ({
  display: 'flex',
  alignItems: 'center',
  gap: theme.spacing(1),
  marginTop: theme.spacing(2),
}));

function App() {
  const [city, setCity] = useState('');
  const [weather, setWeather] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [darkMode, setDarkMode] = useState(false);

  const theme = createTheme({
    palette: {
      mode: darkMode ? 'dark' : 'light',
    },
  });

  const fetchWeather = async (city) => {
    try {
      // First, get coordinates for the city using the geocoding API
      const geocodingResponse = await fetch(
        `https://geocoding-api.open-meteo.com/v1/search?name=${encodeURIComponent(city)}&count=1&language=en&format=json`,
        {
          method: 'GET',
          headers: {
            'Accept': 'application/json',
          },
          mode: 'cors'
        }
      );

      if (!geocodingResponse.ok) {
        throw new Error('Failed to fetch location data');
      }

      const geocodingData = await geocodingResponse.json();

      if (!geocodingData.results || geocodingData.results.length === 0) {
        throw new Error('City not found');
      }

      const { latitude, longitude, name, country_code } = geocodingData.results[0];

      // Then, get weather data for those coordinates
      const weatherResponse = await fetch(
        `https://api.open-meteo.com/v1/forecast?latitude=${latitude}&longitude=${longitude}&current=temperature_2m,relative_humidity_2m,apparent_temperature,pressure_msl,wind_speed_10m,visibility,weather_code&daily=sunrise,sunset,weather_code&timezone=auto`,
        {
          method: 'GET',
          headers: {
            'Accept': 'application/json',
          },
          mode: 'cors'
        }
      );

      if (!weatherResponse.ok) {
        throw new Error('Failed to fetch weather data');
      }

      const weatherData = await weatherResponse.json();

      if (!weatherData.current) {
        throw new Error('Invalid weather data received');
      }

      // Transform the data to match our app's format
      return {
        name,
        sys: {
          country: country_code,
          sunrise: new Date(weatherData.daily.sunrise[0]).getTime() / 1000,
          sunset: new Date(weatherData.daily.sunset[0]).getTime() / 1000
        },
        weather: [{
          id: weatherData.current.weather_code,
          description: getWeatherDescription(weatherData.current.weather_code)
        }],
        main: {
          temp: weatherData.current.temperature_2m,
          feels_like: weatherData.current.apparent_temperature,
          humidity: weatherData.current.relative_humidity_2m,
          pressure: weatherData.current.pressure_msl
        },
        wind: {
          speed: weatherData.current.wind_speed_10m
        },
        visibility: weatherData.current.visibility * 1000 // Convert to meters
      };
    } catch (error) {
      if (error.message === 'Failed to fetch') {
        throw new Error('Network error - Please check your internet connection');
      }
      throw error;
    }
  };

  const handleSearch = async () => {
    if (!city.trim()) {
      setError('Please enter a city name');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const data = await fetchWeather(city);
      setWeather(data);
      setError('');
    } catch (err) {
      setWeather(null);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const getWeatherDescription = (code) => {
    // WMO Weather interpretation codes
    const weatherCodes = {
      0: 'Clear sky',
      1: 'Mainly clear',
      2: 'Partly cloudy',
      3: 'Overcast',
      45: 'Foggy',
      48: 'Depositing rime fog',
      51: 'Light drizzle',
      53: 'Moderate drizzle',
      55: 'Dense drizzle',
      61: 'Slight rain',
      63: 'Moderate rain',
      65: 'Heavy rain',
      71: 'Slight snow',
      73: 'Moderate snow',
      75: 'Heavy snow',
      77: 'Snow grains',
      80: 'Slight rain showers',
      81: 'Moderate rain showers',
      82: 'Violent rain showers',
      85: 'Slight snow showers',
      86: 'Heavy snow showers',
      95: 'Thunderstorm',
      96: 'Thunderstorm with slight hail',
      99: 'Thunderstorm with heavy hail'
    };
    return weatherCodes[code] || 'Unknown';
  };

  const getWeatherIcon = (weatherCode) => {
    // WMO Weather interpretation codes
    if (weatherCode === 0 || weatherCode === 1) return 'â˜€ï¸'; // Clear sky
    if (weatherCode === 2) return 'â›…'; // Partly cloudy
    if (weatherCode === 3) return 'â˜ï¸'; // Overcast
    if (weatherCode === 45 || weatherCode === 48) return 'ðŸŒ«ï¸'; // Fog
    if (weatherCode >= 51 && weatherCode <= 55) return 'ðŸŒ§ï¸'; // Drizzle
    if (weatherCode >= 61 && weatherCode <= 65) return 'ðŸŒ¦ï¸'; // Rain
    if (weatherCode >= 71 && weatherCode <= 77) return 'ðŸŒ¨ï¸'; // Snow
    if (weatherCode >= 80 && weatherCode <= 82) return 'ðŸŒ§ï¸'; // Rain showers
    if (weatherCode >= 85 && weatherCode <= 86) return 'ðŸŒ¨ï¸'; // Snow showers
    if (weatherCode >= 95 && weatherCode <= 99) return 'â›ˆï¸'; // Thunderstorm
    return 'â“';
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <StyledContainer maxWidth="md">
        <div className="background-particles" />
        <div className="background-overlay" />
        <Box sx={{ display: 'flex', justifyContent: 'flex-end', mb: 2 }}>
          <IconButton onClick={() => setDarkMode(!darkMode)} color="inherit">
            {darkMode ? <Brightness7Icon /> : <Brightness4Icon />}
          </IconButton>
        </Box>
        <Typography variant="h3" component="h1" gutterBottom align="center" sx={{ 
          color: 'white',
          textShadow: '2px 2px 4px rgba(0,0,0,0.2)',
          fontWeight: 'bold',
          mb: 4
        }}>
          Weather App
        </Typography>

        <SearchBox>
          <TextField
            fullWidth
            variant="outlined"
            placeholder="Enter city name"
            value={city}
            onChange={(e) => setCity(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
            error={!!error}
            sx={{ 
              backgroundColor: theme.palette.mode === 'dark' 
                ? 'rgba(30, 30, 30, 0.9)'
                : 'rgba(255, 255, 255, 0.9)',
              borderRadius: '10px',
              '& .MuiOutlinedInput-root': {
                borderRadius: '10px',
              },
              '& .MuiInputBase-input': {
                color: theme.palette.mode === 'dark' ? 'white' : 'inherit',
              }
            }}
          />
          <Button
            variant="contained"
            onClick={handleSearch}
            disabled={loading}
            sx={{
              borderRadius: '10px',
              backgroundColor: theme.palette.mode === 'dark' ? '#1a237e' : '#0083b0',
              '&:hover': {
                backgroundColor: theme.palette.mode === 'dark' ? '#121858' : '#006d94',
              }
            }}
          >
            <SearchIcon />
          </Button>
        </SearchBox>

        {error && (
          <Alert 
            severity="error" 
            sx={{ 
              mb: 2,
              borderRadius: '10px',
              backgroundColor: theme.palette.mode === 'dark'
                ? 'rgba(40, 20, 20, 0.9)'
                : 'rgba(253, 237, 237, 0.9)'
            }}
          >
            {error}
          </Alert>
        )}

        {loading ? (
          <Box display="flex" justifyContent="center" mt={4}>
            <CircularProgress sx={{ color: 'white' }} />
          </Box>
        ) : weather && (
          <WeatherCard>
            <CardContent>
              <Typography variant="h4" component="h2" gutterBottom align="center">
                {weather.name}, {weather.sys.country}
              </Typography>

              <Box display="flex" justifyContent="center" mb={2}>
                <Typography variant="h2" component="div">
                  {getWeatherIcon(weather.weather[0].id)}
                </Typography>
              </Box>

              <Grid container spacing={3}>
                <Grid item xs={12} md={6}>
                  <Box display="flex" flexDirection="column" alignItems="center">
                    <Typography variant="h1" component="div">
                      {Math.round(weather.main.temp)}Â°C
                    </Typography>
                    <Typography variant="h5" color="textSecondary" sx={{ textTransform: 'capitalize' }}>
                      {weather.weather[0].description}
                    </Typography>
                  </Box>
                </Grid>

                <Grid item xs={12} md={6}>
                  <Box>
                    <WeatherInfo>
                      <ThermostatIcon />
                      <Typography>Feels like: {Math.round(weather.main.feels_like)}Â°C</Typography>
                    </WeatherInfo>
                    <WeatherInfo>
                      <WaterDropIcon />
                      <Typography>Humidity: {weather.main.humidity}%</Typography>
                    </WeatherInfo>
                    <WeatherInfo>
                      <AirIcon />
                      <Typography>Wind: {weather.wind.speed} m/s</Typography>
                    </WeatherInfo>
                    <WeatherInfo>
                      <CompressIcon />
                      <Typography>Pressure: {weather.main.pressure} hPa</Typography>
                    </WeatherInfo>
                    <WeatherInfo>
                      <VisibilityIcon />
                      <Typography>Visibility: {(weather.visibility / 1000).toFixed(1)} km</Typography>
                    </WeatherInfo>
                  </Box>
                </Grid>
              </Grid>

              <Divider sx={{ my: 3 }} />

              <Grid container spacing={2} justifyContent="center">
                <Grid item xs={6} sm={3}>
                  <Box textAlign="center">
                    <Typography variant="h6" color="textSecondary">
                      Sunrise
                    </Typography>
                    <Typography>
                      {new Date(weather.sys.sunrise * 1000).toLocaleTimeString()}
                    </Typography>
                  </Box>
                </Grid>
                <Grid item xs={6} sm={3}>
                  <Box textAlign="center">
                    <Typography variant="h6" color="textSecondary">
                      Sunset
                    </Typography>
                    <Typography>
                      {new Date(weather.sys.sunset * 1000).toLocaleTimeString()}
                    </Typography>
                  </Box>
                </Grid>
              </Grid>

              <Box mt={2} textAlign="center">
                <Typography variant="caption" color="textSecondary">
                  Data source: Open-Meteo
                </Typography>
              </Box>
            </CardContent>
          </WeatherCard>
        )}
      </StyledContainer>
    </ThemeProvider>
  );
}

export default App;
